    switch (name) {
      case 'rainbow': {
        this.rainbow = newValue == '';
        break;
      }
      case 'lang': {
        const language = this.getAttribute('lang');
        if (helloTranslations[language] != undefined) {
          this.lang = language;
        }
      }
