/**
 * <element-image> component
 */
class CoreProgress extends HTMLElement {
  /**
   * constructor
   */
  constructor() {
    super();

    this._type = this.getAttribute('type') || 'line';
    this._percentage = this.getAttribute('percentage') || 0;
    this._status = this.getAttribute('status') || undefined;
    this._strokeWidth = this.getAttribute('stroke-width') || 6;
    this._textInside = this.hasAttribute('text-inside') || false;
    this._width = this.getAttribute('width') || 126;
    this._showText = this.hasAttribute('show-text') || false;
    this._color = this.getAttribute('color') || undefined;

    this.attachShadow({mode: 'open'});
    this._template = document.createElement('template');
    this._updateTemplate();
  }

  /**
   * type getter
   */
  get type() {
    return this._type;
  }

  /**
   * type setter
   * @param {*} val
   */
  set type(val) {
    if (['line', 'circle'].indexOf(val) > -1) {
      this._type = val;
      this.setAttribute('type', val);
    } else {
      this._type = 'line';
      this.setAttribute('type', 'line');
    }

    this._updateTemplate();
  }

  /**
   * percentage setter
   */
  get percentage() {
    return this._percentage;
  }

  /**
   * percentage setter
   * @param {*} val
   */
  set percentage(val) {
    if (typeof val === 'number' && val <= 100 && val >= 0) {
      this._percentage = val;
      this.setAttribute('percentage', val);
    } else {
      this._percentage = 0;
      this.setAttribute('percentage', 0);
    }

    this._updateTemplate();
  }

  /**
   * status getter
   */
  get status() {
    return this._status;
  }

  /**
   * status setter
   * @param {*} val
   */
  set status(val) {
    if (['success', 'exception', 'text'].indexOf(val) > -1) {
      this._status = val;
      this.setAttribute('status', val);
    } else {
      this._status = undefined;
      this.removeAttribute('status');
    }

    this._updateTemplate();
  }

  /**
   * strokeWidth getter
   */
  get strokeWidth() {
    return this._strokeWidth;
  }

  /**
   * strokeWidth setter
   * @param {*} val
   */
  set strokeWidth(val) {
    if (typeof val === 'number') {
      this._strokeWidth = val;
      this.setAttribute('stroke-width', val);
    } else {
      this._strokeWidth = 6;
      this.setAttribute('stroke-width', 6);
    }

    this._updateTemplate();
  }

  /**
   * textInside getter
   */
  get textInside() {
    return this._textInside;
  }

  /**
   * textInside setter
   * @param {*} val
   */
  set textInside(val) {
    if (val === true) {
      this._textInside = true;
      this.setAttribute('text-inside', '');
    } else {
      this._textInside = false;
      this.removeAttribute('text-inside');
    }

    this._updateTemplate();
  }

  /**
   * width getter
   */
  get width() {
    return this._width;
  }

  /**
   * width setter
   * @param {*} val
   */
  set width(val) {
    if (typeof val === 'number') {
      this._width = val;
      this.setAttribute('width', val);
    } else {
      this._width = 126;
      this.setAttribute('width', 126);
    }

    this._updateTemplate();
  }

  /**
   * showText getter
   */
  get showText() {
    return this._showText;
  }

  /**
   * showText setter
   * @param {*} val
   */
  set showText(val) {
    if (val === true) {
      this._showText = true;
      this.setAttribute('show-text', '');
    } else {
      this._showText = false;
      this.removeAttribute('show-text');
    }

    this._updateTemplate();
  }

  /**
   * color getter
   */
  get color() {
    return this._color;
  }

  /**
   * color setter
   * @param {*} val
   */
  set color(val) {
    if (typeof val === 'string') {
      this._color = val;
      this.setAttribute('color', val);
    } else {
      this._color = undefined;
      this.removeAttribute('color');
    }

    this._updateTemplate();
  }

  /**
   * _updateTemplate()
   */
  _updateTemplate() {
    let progressBar = '';
    let progressCircle = '';

    if (this.type === 'line') {
      progressBar = `
        <div class="el-progress-bar">
          <div 
            class="el-progress-bar__outer" 
            style="height: ${this.strokeWidth + 'px'};"
          >
            <div class="el-progress-bar__inner" style="${this.barStyle}">
              ${(this.showText && this.textInside) ? `<div class="el-progress-bar__innerText">${this.percentage}%</div>` : ''}
            </div>
          </div>
        </div>
      `;
    } else {
      progressCircle = `
        <div class="el-progress-circle" style="height: ${this.width + 'px'}; width: ${this.width + 'px'}">
          <svg viewBox="0 0 100 100">
            <path 
              class="el-progress-circle__track" 
              d="${this.trackPath}" 
              stroke="#e5e9f2" 
              stroke-width=${this.relativeStrokeWidth} 
              fill="none">
            </path>
            <path 
              class="el-progress-circle__path" 
              d="${this.trackPath}" 
              stroke-linecap="round" 
              stroke=${this.stroke} 
              stroke-width=${this.relativeStrokeWidth} 
              fill="none" 
              style="${this.circlePathStyle}">
            </path>
          </svg>
        </div>
      `;
    }

    this._template.innerHTML = `
      <div
        class="${`el-progress el-progress--${this.type} 
          ${this.status !== undefined ? `is-${this.status}` : ``} 
          ${!this.showText ? 'el-progress--without-text' : ''} 
          ${this.textInside ? 'el-progress--text-inside' : ''}`}"
        role="progressbar"
        aria-valuenow=${this.percentage}
        aria-valuemin="0"
        aria-valuemax="100"
      >
        ${progressBar}
        ${progressCircle}
        ${(this.showText && !this.textInside) ? ` 
          <div class="el-progress__text" style="font-size: ${this.progressTextSize + 'px'}">
            ${this.status === undefined ? `${this.percentage}%` : `<i class=${this.iconClass}></i>`}
          </div>` : ''}
      </div>
    `;

    this.shadowRoot.innerHTML = '';
    this.shadowRoot.appendChild(this._template.content);
    this.shadowRoot.appendChild(style.cloneNode(true));
  }

  /**
   * barStyle getter
   */
  get barStyle() {
    return `width: ${this.percentage + '%'};` + ` ${this.color === null ? '' : 'background-color: ' + this.color + ';'}`;
  }

  /**
   * relativeStrokeWidth getter
   */
  get relativeStrokeWidth() {
    return (this.strokeWidth / this.width * 100).toFixed(1);
  }

  /**
   * trackPath getter
   */
  get trackPath() {
    const radius = parseInt(50 - parseFloat(this.relativeStrokeWidth) / 2, 10);
    return `M 50 50 m 0 -${radius} a ${radius} ${radius} 0 1 1 0 ${radius * 2} a ${radius} ${radius} 0 1 1 0 -${radius * 2}`;
  }

  /**
   * perimeter getter
   */
  get perimeter() {
    const radius = 50 - parseFloat(this.relativeStrokeWidth) / 2;
    return 2 * Math.PI * radius;
  }

  /**
   * circlePathStyle getter
   */
  get circlePathStyle() {
    const perimeter = this.perimeter;
    return `stroke-dasharray: ${perimeter}px ${perimeter}px; ` +
      `stroke-dashoffset: ${(1 - this.percentage / 100) * perimeter + 'px'}; ` +
      `transition: stroke-dashoffset 0.6s ease 0s, stroke 0.6s ease;`;
  }

  /**
   * stroke getter
   */
  get stroke() {
    let ret;
    if (this.color) {
      ret = this.color;
    } else {
      switch (this.status) {
        case 'success':
          ret = '#13ce66';
          break;
        case 'exception':
          ret = '#ff4949';
          break;
        default:
          ret = '#20a0ff';
      }
    }
    return ret;
  }

  /**
   * iconClass getter
   */
  get iconClass() {
    if (this.type === 'line') {
      return this.status === 'success' ? 'el-icon-circle-check' : 'el-icon-circle-close';
    } else {
      return this.status === 'success' ? 'el-icon-check' : 'el-icon-close';
    }
  }

  /**
   * progressTextSize getter
   */
  get progressTextSize() {
    return this.type === 'line'
      ? 12 + this.strokeWidth * 0.4
      : this.width * 0.111111 + 2;
  }
}

if (!customElements.get('core-progress')) {
  customElements.define('core-progress', CoreProgress);
}

import uuid from 'uuid/v1';
import templateString from './core-slider.html';
import styleString from './core-slider.css';
import '../core-button/core-button';
import '../core-modal/core-modal';

// Create template element
const template = document.createElement('template');
template.innerHTML = templateString;

// Create style element
const style = document.createElement('style');
style.innerHTML = styleString;

/**
 * ImageSlider Class
 */
class coreSlider extends HTMLElement {
  /**
   * constructor
   */
  constructor() {
    super();
    // Set the default prop values
    this._index = 0;
    this._controlInitialized = false;

    // Create the shadow dom
    const shadowRoot = this.attachShadow({mode: 'open'});
    shadowRoot.appendChild(template.content.cloneNode(true));
    shadowRoot.appendChild(style.cloneNode(true));

    // Store slider link
    this._slider = this.shadowRoot.getElementById('slider');
  }

  /**
   * _handleLongText
   */
  _handleLongText() {
    const content = this._content.innerHTML;
    const title = this._title.innerHTML;
    const size = this.getAttribute('size');
    const theme = this.getAttribute('theme');
    const themeScale = theme === 'circle' ? 0.5 : 1;

    let needHide = false;
    let buttonSize;

    switch (size) {
      case 'small': {
        const limit = 30;
        buttonSize = 'tiny';
        if (content.length > limit * themeScale) {
          this._content.innerHTML = content.substring(0, limit) + '...';
          needHide = true;
        }
        break;
      }

      case 'medium': {
        const limit = 80;
        buttonSize = theme === 'circle' ? 'tiny' : 'xsmall';
        if (content.length > limit * themeScale) {
          this._content.innerHTML = content.substring(0, limit) + '...';
          needHide = true;
        }
        break;
      }

      case 'large': {
        const limit = 200;
        buttonSize = theme === 'circle' ? 'xsmall' : 'small';
        if (content.length > limit * themeScale) {
          this._content.innerHTML = content.substring(0, limit) + '...';
          needHide = true;
        }
        break;
      }

      default: break;
    }

    if (needHide) {
      this._hideContent(buttonSize, theme, title, content);
    }
  }

  /**
   * _hideContent
   * @param {*} buttonSize
   * @param {*} theme
   * @param {*} title
   * @param {*} content
   */
  _hideContent(buttonSize, theme, title, content) {
    // generate a modal name
    const id = uuid();

    // create a button linked to the modal
    const button = document.createElement('core-button');
    button.setAttribute('rounded', true);
    button.setAttribute('size', buttonSize);
    // button.setAttribute('theme', 'standard');
    button.setAttribute('color', 'secondary');
    button.setAttribute('modal', id);

    if (theme !== 'circle') {
      button.setAttribute('style', 'float: right; margin: 10px 20px;');
    } else {
      button.setAttribute('style', 'margin: 20px 20px;');
      this._content.style['align-items'] = 'center';
    }

    button.innerHTML = 'Read more';
    this._content.appendChild(button);

    // create a modal to show all description
    const modal = document.createElement('core-modal');
    modal.setAttribute('name', id);
    modal.innerHTML = `
      <h1 slot="header">${title}</h1>
      <p>${content}</p>
    `;
    document.body.appendChild(modal);
  }

  /**
   * observedAttributes getter
   */
  static get observedAttributes() {
    return [
      'time',
      'size',
      'theme',
      'shadow',
      'text',
      'control',
    ];
  }

  /**
   * size setter
   * @param {*} val
   */
  set size(val) {
    if (val) {
      this.setAttribute('size', val);
    } else {
      this.removeAttribute('size');
    }
  }

  /**
   * theme setter
   * @param {*} val
   */
  set theme(val) {
    if (val) {
      this.setAttribute('theme', val);
    } else {
      this.removeAttribute('theme');
    }
  }

  /**
   * shadow setter
   * @param {*} val
   */
  set shadow(val) {
    if (val === true) {
      this.setAttribute('shadow', '');
    } else {
      this.removeAttribute('shadow');
    }
  }

  /**
   * text setter
   * @param {*} val
   */
  set text(val) {
    if (val === true) {
      this.setAttribute('text', '');
    } else {
      this.removeAttribute('text');
    }
  }

  /**
   * control setter
   * @param {*} val
   */
  set control(val) {
    if (val === true) {
      this.setAttribute('control', '');
    } else {
      this.removeAttribute('control');
    }
  }

  /**
   * size getter
   */
  get size() {
    return this.getAttribute('size');
  }

  /**
   * theme getter
   */
  get theme() {
    return this.getAttribute('theme');
  }

  /**
   * shadow getter
   */
  get shadow() {
    return this.getAttribute('shadow') === '';
  }

  /**
   * text getter
   */
  get text() {
    return this.getAttribute('text') === '';
  }

  /**
   * control getter
   */
  get control() {
    return this.getAttribute('control') === '';
  }

  /**
   * connectedCallback
   */
  connectedCallback() {
    // Find all image children
    this._imageChildren = this.getElementsByTagName('img');
    this._imageChildCount = this._imageChildren.length;

    // change to setInterval to avoid recursion
    this._start(0);
  }

  /**
   * Initialize control
   */
  _initControl() {
    // Find all image children
    this._imageChildren = this.getElementsByTagName('img');
    this._imageChildCount = this._imageChildren.length;

    // Initialize menu
    this._initMenu();

    // Initialize arrows
    this._initArrows();
  }

  /**
   * _initMenu()
   */
  _initMenu() {
    let menuHTML = '';

    for (let i = 0; i < this._imageChildCount; i ++) {
      menuHTML += `<div index=${i} class="menu-item"></div>`;
    }

    const menu = document.createElement('div');
    menu.setAttribute('id', 'menu');
    menu.innerHTML = menuHTML;

    Array.from(menu.children).forEach((child) => {
      child.addEventListener('click', () => {
        this._start(child.getAttribute('index'));
      });
    });

    this._menuItems = Array.from(menu.children);
    this._menu = menu;
  }

  /**
   * _initArrows()
   */
  _initArrows() {
    const leftArrow = document.createElement('div');
    const rightArrow = document.createElement('div');

    leftArrow.setAttribute('id', 'left-arrow');
    leftArrow.classList.add('arrow');

    rightArrow.setAttribute('id', 'right-arrow');
    rightArrow.classList.add('arrow');

    leftArrow.addEventListener('click', () => {
      this.index -= 1;
      this._start(this.index);
    });

    rightArrow.addEventListener('click', () => {
      this.index += 1;
      this._start(this.index);
    });
    this._leftArrow = leftArrow;
    this._rightArrow = rightArrow;
  }

  /**
   * attributeChangedCallback
   * @param {*} attr
   * @param {*} oldV
   * @param {*} newV
   */
  attributeChangedCallback(attr, oldV, newV) {
    switch (attr) {
      case 'time': {
        this._slider.style.transitionDuration = newV;
        break;
      }

      case 'control': {
        this._setUpControl(oldV);
      }

      case 'text': {
        // Set up read more button;
        if (this.text && this._title === undefined && this._content === undefined) {
          this._title = this.querySelector('[slot="title"]');
          this._content = this.querySelector('[slot="content"]');

          if (this.text && this._title !== null && this._content !== null) {
            this._handleLongText();
          }
        }
      }

      default: break;
    }
  }

  /**
   * time getter
   */
  get time() {
    return this.getAttribute('time') || '2s';
  }

  /**
   * time setter
   * @param {*} val
   */
  set time(val) {
    if (val) {
      this.setAttribute('time', val);
    } else {
      this.setAttribute('time', '2s');
    }
  }

  /**
   * index getter
   */
  get index() {
    return this._index;
  }

  /**
   * index setter
   * @param {Number} v
   */
  set index(v) {
    this._index = v;
    // For looping
    if (this._index >= this._imageChildCount) this.index = 0;
    if (this._index < 0) this.index = Number(this._imageChildCount) - 1;
  }

  /**
   * start from a specific picture
   * @param {Number} index
   */
  _start(index) {
    if (this._interval !== undefined) {
      clearInterval(this._interval);
    }

    this._reRender(index);

    this.index = index;
    this._interval = setInterval(this._tick.bind(this), parseFloat(this.time) * 1000);
  }

  /**
   * tick function
   */
  _tick() {
    this.index = Number(this.index) + 1;
    this._reRender(this.index);
  }

  /**
   * @param {Number} index
   */
  _reRender(index) {
    Array.from(this._imageChildren).forEach((child) => {
      child.classList.add('hide');
    });
    this._imageChildren[index].classList.remove('hide');

    if (this.control && this._menuItems !== null) {
      this._menuItems.forEach((item) => {
        item.removeAttribute('selected', '');
      });
      this._menuItems[index].setAttribute('selected', '');
    }
  }

  /**
   * _setUpControl
   * @param {*} oldV
   */
  _setUpControl(oldV) {
    // Set up control
    if (!this._controlInitialized) {
      this._initControl();
      this._controlInitialized = false;
    }

    if (this.control) {
      this._slider.appendChild(this._menu);
      this._slider.appendChild(this._leftArrow);
      this._slider.appendChild(this._rightArrow);
    } else if (oldV === '') {
      this._slider.removeChild(this._slider.querySelector('#menu'));
      this._slider.removeChild(this._slider.querySelector('#left-arrow'));
      this._slider.removeChild(this._slider.querySelector('#right-arrow'));
    }
  }
}

if (!customElements.get('core-slier')) {
  customElements.define('core-slider', coreSlider);
}
